import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const [rewrittenTickets, setRewrittenTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/projects`);
      setProjects(response.data);
    } catch (err) {
      addMessage('Failed to fetch projects. Please check your API connection.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleProjectSelect = async (project) => {
    setSelectedProject(project);
    setSelectedTickets([]);
    setRewrittenTickets([]);
    setLoading(true);

    try {
      const response = await axios.get(`${API_BASE_URL}/projects/${project.key}/issues`);
      const formattedTickets = response.data.map(ticket => ({
        ...ticket,
        isRewritten: false
      }));
      setTickets(formattedTickets);
    } catch (err) {
      addMessage(`Failed to fetch tickets for project ${project.name}.`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleTicketSelect = (ticket) => {
    setSelectedTickets(prev => 
      prev.some(t => t.key === ticket.key) 
        ? prev.filter(t => t.key !== ticket.key) 
        : [...prev, ticket]
    );
  };

  const handleRewriteTickets = async () => {
    if (selectedTickets.length === 0) {
      addMessage('Please select at least one ticket to rewrite.', 'error');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(`${API_BASE_URL}/rewrite-tickets`, selectedTickets);
      setRewrittenTickets(response.data);
      addMessage('Tickets rewritten successfully!', 'success');

      const updatedTickets = tickets.map(ticket => 
        selectedTickets.some(t => t.key === ticket.key)
          ? { ...ticket, isRewritten: true }
          : ticket
      );
      setTickets(updatedTickets);
    } catch (err) {
      addMessage('Failed to rewrite tickets. Please try again.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateTickets = async () => {
    setLoading(true);

    try {
      const response = await axios.put(`${API_BASE_URL}/update-tickets`, {
        tickets: rewrittenTickets
      });

      if (response.data.success) {
        addMessage('Tickets updated successfully in Jira!', 'success');
        setRewrittenTickets([]);
        setSelectedTickets([]);
      } else {
        addMessage(`Some tickets failed to update: ${response.data.failed_tickets.map(t => t.key).join(', ')}`, 'error');
      }
    } catch (err) {
      addMessage('Failed to update tickets in Jira. Please try again.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const addMessage = (message, type) => {
    setMessages(prev => [...prev, { message, type }]);
  };

  const clearMessage = (index) => {
    setMessages(prev => prev.filter((_, i) => i !== index));
  };

  const handleDescriptionChange = (key, newDescription) => {
    setRewrittenTickets(prev => 
      prev.map(ticket => 
        ticket.key === key ? { ...ticket, rewritten_description: newDescription } : ticket
      )
    );
  };

  return (
    <div className="App">
      <div className={`sidebar ${sidebarCollapsed ? 'collapsed' : ''}`}>
        <div className="sidebar-header">
          <h3>Jira Tools</h3>
          <button onClick={() => setSidebarCollapsed(!sidebarCollapsed)}>
            {sidebarCollapsed ? '‚ùØ' : '‚ùÆ'}
          </button>
        </div>
        <div className="sidebar-content">
          <nav>
            <ul>
              <li><a href="#projects">Projects</a></li>
              <li><a href="#tickets">Tickets</a></li>
              <li><a href="#rewritten">Rewritten Tickets</a></li>
            </ul>
          </nav>
          <div className="sidebar-actions">
            <button 
              className="action-button primary"
              onClick={handleUpdateTickets}
              disabled={loading}
            >
              Update Ticket
            </button>
            <button 
              className="action-button secondary"
              onClick={handleRewriteTickets}
              disabled={loading || selectedTickets.length === 0}
            >
              Rewrite Ticket
            </button>
          </div>
        </div>
      </div>

      <main className="App-main">
        <header className="App-header">
          <h1>Jira Ticket Rewriter</h1>
          <button className="action-button icon-button" onClick={() => window.location.reload()}>
            <span className="button-icon">üîÑ</span>
          </button>
        </header>

        <section className="projects-section">
          <h2>Projects</h2>
          {loading && projects.length === 0 ? (
            <div className="loading-indicator">Loading projects...</div>
          ) : (
            <div className="project-list">
              {projects.map(project => (
                <div 
                  key={project.id} 
                  className={`project-card ${selectedProject?.id === project.id ? 'active' : ''}`}
                  onClick={() => handleProjectSelect(project)}
                >
                  <h3>{project.name}</h3>
                  <div className="project-meta">
                    <span>Key: {project.key}</span>
                    <span>Type: {project.projectTypeKey}</span>
                  </div>
                  {selectedProject?.id === project.id && (
                    <span className="selected-indicator">Selected</span>
                  )}
                </div>
              ))}
            </div>
          )}
        </section>

        {selectedProject && (
          <div className="content-wrapper">
            <section className="tickets-section">
              <div className="section-header">
                <h2>Jira Tickets</h2>
                <button 
                  className="action-button secondary"
                  onClick={() => handleProjectSelect(selectedProject)}
                  disabled={loading}
                >
                  <span className="button-icon">üîÑ</span> Refresh
                </button>
              </div>

              <div className="selection-controls">
                <label>
                  <input
                    type="checkbox"
                    onChange={() => {
                      if (selectedTickets.length === tickets.length) {
                        setSelectedTickets([]);
                      } else {
                        setSelectedTickets(tickets);
                      }
                    }}
                  />
                  Select All
                </label>
                <span>{`${selectedTickets.length} of ${tickets.length} selected`}</span>
              </div>

              {loading && tickets.length === 0 ? (
                <div className="loading-indicator">Loading tickets...</div>
              ) : (
                <div className="ticket-list">
                  {tickets.map(ticket => (
                    <div key={ticket.key} className="ticket-card">
                      <input
                        type="checkbox"
                        checked={selectedTickets.some(t => t.key === ticket.key)}
                        onChange={() => handleTicketSelect(ticket)}
                      />
                      <div className="ticket-content">
                        <h3>{ticket.key}: {ticket.summary}</h3>
                        <p style={{ color: '#222222', fontWeight: 600 }}>
                          {ticket.description || 'No description provided'}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              <button 
                className="action-button primary"
                onClick={handleRewriteTickets}
                disabled={loading || selectedTickets.length === 0}
              >
                <span className="button-icon">üìù</span> Rewrite Selected Tickets
              </button>
            </section>

            <section className="rewritten-section">
              <h2>Rewritten Tickets</h2>

              {loading && (
                <div className="loading-indicator">Processing tickets...</div>
              )}

              {rewrittenTickets.length > 0 ? (
                <>
                  <div className="rewritten-list">
                    {rewrittenTickets.map(ticket => (
                      <div key={ticket.key} className="rewritten-card">
                        <h3>Original: {ticket.original_title}</h3>
                        <h4>Rewritten: {ticket.rewritten_title}</h4>
                        <textarea
                          value={ticket.rewritten_description}
                          onChange={(e) => handleDescriptionChange(ticket.key, e.target.value)}
                          placeholder="Edit rewritten description..."
                          rows={4}
                        />
                        <div className="acceptance-criteria">
                          <h5>Acceptance Criteria:</h5>
                          <ul>
                            {ticket.acceptance_criteria.map((criterion, index) => (
                              <li key={index}>{criterion}</li>
                            ))}
                          </ul>
                        </div>
                      </div>
                    ))}
                  </div>
                  <button 
                    className="action-button success"
                    onClick={handleUpdateTickets}
                    disabled={loading}
                  >
                    <span className="button-icon">‚úÖ</span> Approve & Update Jira
                  </button>
                </>
              ) : (
                <div className="empty-state">
                  <p>No rewritten tickets yet. Select tickets and click 'Rewrite Selected Tickets'.</p>
                </div>
              )}
            </section>
          </div>
        )}

        <div className="messages">
          {messages.map((message, index) => (
            <div 
              key={index} 
              className={`message-box ${message.type}`}
              style={{ backgroundColor: message.type === 'success' ? '#e8f5e9' : '#ffebee' }}
            >
              <span>{message.message}</span>
              <button onClick={() => clearMessage(index)} className="clear-button">‚úï</button>
            </div>
          ))}
        </div>
      </main>

      <footer className="App-footer">
        <div className="footer-content">
          <span>¬© 2025 Jira Ticket Rewriter</span>
          <span>Built with React & FastAPI</span>
        </div>
      </footer>
    </div>
  );
}

export default App;
